import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EmailForm2 = () => {
    const [serverState, setServerState] = useState({
        sending: false,
        status: null
    });
    const [fieldErrors, setFieldErrors] = useState({});
    const [inputs, setInputs] = useState({
        email: '',
        message: ''
    });
    const validityRules = {
        email: val => {val && /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(val)},
        message: val => !!val
    };
    const validate = () => {
        let errors = {
            email: false,
            message: false
        };
        let hasErrors = false;
        for (let key of Object.keys(inputs)) {
            errors[key] = !validityRules[key](inputs[key]);
            hasErrors |= errors[key];
        }
        setFieldErrors(prev => ({ ...prev, ...errors }));
        return !hasErrors;
    };
    const renderFieldError = field => {
        console.log(!fieldErrors)[field];

        // if (!fieldErrors[field]) {
        //     return <p className="errorMsg">Please enter a valid {field}</p>
        // }
    };
    useEffect(() => {
        if (Object.keys(fieldErrors).length > 0) {
          validate();
        }
      }, [inputs]);

    const handleOnChange = event => {
        event.persist();
        setInputs(prev => ({
            ...prev,
            [event.target.id]: event.target.value
        }));
    };

    const handleServerResponse = (ok, msg) => {
        setServerState({
            sending: false,
            status: { ok, msg }
        });
        if (ok) {
            setFieldErrors({});
            setInputs({
                email: '',
                message: ''
            })
        }
    };

    const handleFormSubmit = e => {
        if (!validate()) {
            return;
        }
        e.preventDefault();
        setServerState({ sending: true });
        axios({
            method: 'post',
            url: 'https://formspree.io/mbjllqae',
            data: new FormData(form)
        })
            .then(resp => {
                handleServerResponse(true, 'Message sent. Thanks!');
            })
            .catch(resp => {
                handleServerResponse(false, resp.response.data.error, form);
            });
    };
    return (
        <div>
            <h2>GET IN TOUCH</h2>
            <form onSubmit={handleFormSubmit}>
                <label htmlFor='email'>Email</label>
                <input
                    value={inputs.email}
                    onChange={handleOnChange}
                    id='email'
                    type='email'
                    name='email'
                    noValidate
                    autoComplete="off"
                    required
                    />
                {renderFieldError("email")}
                <label htmlFor='message'>Message</label>
                <textarea
                    value={inputs.message}
                    onChange={handleOnChange}
                    id='message'
                    type='text'
                    name='message'
                    rows='2'
                    noValidate
                    required
                />
                {renderFieldError('message')}
                <button type='submit' disable={serverState.sending.toString()} onClick={handleFormSubmit}>SEND</button>
                {serverState.status && (
                    <p className={!serverState.status.ok ? "errorMsg" : ""}>{serverState.status.msg}</p>
                )}
            </form>
            <style jsx>{`
        form {
          display: flex;
          flex-direction: column;
          justify-content: flex-start;
          align-items: space-between;
          padding-bottom: 1rem;
        }
        form label {
          text-align: left;
        }
        label, button {
          margin-top: 1rem;
        }
        input, textarea {
          margin-top: .5rem;
        }
        form textarea {
          resize: vertical;
        }
        button {
          color: #fff;
          margin-top: 2rem;
          background: #d81e5b;
          padding: 1rem 1.5rem;
          border: 2px solid #fff;
        }
      `}</style>
        </div>
    )
}

export default EmailForm2;
