import React from "react";
// import EmailForm2 from "./EmailForm2";


export default class MyForm extends React.Component {
  constructor(props) {
    super(props);
    this.submitForm = this.submitForm.bind(this);
    this.state = {
      status: ""
    };
    // add function to check form validation, if all clear, then send
  }


  render() {
    const { status } = this.state;
    return (
      <div>
        <h2>DROP US A LINE?</h2>
        <form
          // call validation function first
          onSubmit={this.submitForm}
          action="https://formspree.io/mbjllqae"
          method="POST"
        >
          <label>Email</label>
          <input type="email" name="email" />
          <label>Message</label>
          <textarea type="text" name="message" />
          {status === "SUCCESS" ? <p>Thanks!</p> : <button>SUBMIT</button>}
          {status === "ERROR" && <p>Oops, tech fail. Try emailing us at thebellflowerband@gmail.com</p>}
          <style jsx>{`
            form {
              display: flex;
              flex-direction: column;
              justify-content: flex-start;
              align-items: space-between;
              padding-bottom: 1rem;
            }
            form label {
              text-align: left;
            }
            label, button {
              margin-top: 1rem;
            }
            button {
              margin-top: 2rem;
            }
            input, textarea {
              margin-top: .5rem;
            }
            form textarea {
              resize: vertical;
            }
            button {
              color: #fff;
              background: #d81e5b;
              padding: 1rem 1.5rem;
              border: 2px solid #fff;
          }
          `}</style>
        </form>
      </div>
    );
  }

  submitForm(ev) {
    ev.preventDefault();
    const form = ev.target;
    const data = new FormData(form);
    const xhr = new XMLHttpRequest();
    xhr.open(form.method, form.action);
    xhr.setRequestHeader("Accept", "application/json");
    xhr.onreadystatechange = () => {
      if (xhr.readyState !== XMLHttpRequest.DONE) return;
      if (xhr.status === 200) {
        form.reset();
        this.setState({ status: "SUCCESS" });
      } else {
        this.setState({ status: "ERROR" });
      }
    };
    xhr.send(data);
  }
}

// export default MyForm;
