import Link from 'next/link';

const CtaStyle = `
    color: #fff;
    background: #d81e5b;
    padding: 1rem 1.5rem;
    border: 2px solid #fff;
    text-align: center;
    @media screen only and (orientation: portrait) {
        [data-link-to="/albums"] {
            align-items: flex-start;
            background: red;
        }
    }
`;

const Cta = (props) => {
    if (props.type === 'link') {
        return (
            <div className="cta" data-link-to={props.goTo}>
                <Link href={props.goTo}><a>{props.text}</a></Link>
                <style jsx>{`
                    a {
                        ${CtaStyle}
                    }
                    [data-link-to="/albums"] {
                        grid-area: album;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                      }
                      [data-link-to="/media"] {
                        grid-area: video;
                    }
                    @media screen only and (orientation: portrait) {
                        [data-link-to="/albums"] {
                            align-items: flex-start;
                            background: red;
                        }
                    }
                `}
                </style>
            </div>
        )
    } else {
        return (
            <div className="cta">
                <button type="button">{props.text}</button>
                <style jsx>{`
                    button {
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        ${CtaStyle}
                    }
                `}
                </style>
            </div>
        )
    }
}

export default Cta;
