import StyleBase from '../components/StyleBase';
import { useState } from 'react';

const Media = () => {
    const [posterNum, setPosterNum] = useState(1);
    const posterCycle = (num) => {
        if (num === 3) {
            setPosterNum(1);
        } else {
            setPosterNum(posterNum + 1);
        }
    }
    return (
        <StyleBase>
            <section>
                <h2>VIDEO</h2>
                <p>SPARKcon Music Festival</p>
                <div className="videoContainer">
                    <div className="loading">Loading</div>
                    <div className="container"><iframe scrolling src="https://www.youtube.com/embed/Ofv5mz3Jvak" frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe></div>
                </div>
                {/* <div className="embedContainer">
                    <div className="loading">Loading</div>
                    <div className=""><iframe scrolling src="https://www.youtube.com/embed/Ofv5mz3Jvak" frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe></div>
                </div> */}
                <p>Carrboro Music Festival</p>
                <div className="videoContainer">
                    <div className="loading">Loading</div>
                    <iframe src="https://www.youtube.com/embed/M_JR38PYAgs" frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>
                </div>
                <p>WHUP Radio</p>
                <div className="videoContainer">
                    <div className="loading">Loading</div>
                    <iframe src="https://www.youtube.com/embed/Ojt7JEuhWyM" frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>
                </div>
                <h2>POSTERS</h2>
                <img className="poster" onClick={() => {posterCycle(posterNum)}} src={require(`../public/bellflowerPoster${posterNum}.jpg`)} />
                <style jsx>{`
                    .container {
                        z-index: 2;
                    }
                    iframe {
                    width: calc(100vw - 2rem);
                    height: calc(56.25vw - 2rem -4px);
                    border: 2px solid #f69d1a;
                    display: flex;
                    justify-content: center;
                    position: absolute;
                    top: 0;
                    left: 0;
                    z-index: 2;
                    }
                    .videoContainer {
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                        align-items: center;
                        margin: 2rem 0;
                        position: relative;
                        // padding-bottom: 50%;
                        // padding-top: 25px;
                        width: calc(100vw - 2rem);
                        height: calc(56.25vw - 2rem);
                    }
                    .loading {
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        width: calc(100vw - 2rem);
                        height: calc(56.25vw - 2rem);
                        position: absolute;
                        background: #f69d1a;
                        color: #222;
                        z-index: 1;
                    }
                    audio {
                        width: 100%;
                        border-radius: 30px;
                        border: 2px solid #f69d1a;
                    }
                    .poster {
                        width: 100%;
                    }
                    `}</style>
            </section>
        </StyleBase>
    )
}

export default Media;
